print("\n \t\t Pattern printing")
print("\t\t ----------------")

# printing patterns using single for loop and if else statement
total_iteration = range(1,10)
pattern_string = ""
decreasing_pattern_count = 5
for i in total_iteration:

# prints '*' in increasing pattern for 5 iterations
    
    if i <= 5:
        pattern_string = pattern_string + "*"
        print(pattern_string,"\n")

# prints '*' in dcreasing pattern for 4 iterations

    else:
        print(pattern_string[1:decreasing_pattern_count],"\n")
        decreasing_pattern_count -= 1
   
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           



